import React from 'react'
import './Card.css'
import StarIcon from '@mui/icons-material/Star';
import Button from '@mui/material/Button';

function Card({ thumbnail, title, price, reviews, address, control }) {
    return (
        <div className='card'>
            <img src={thumbnail} alt="" />
            <div className="card__info">
                <h2>{title}</h2>
                <h2>{price}</h2>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <br/>
                <span style={{fontSize:'12px'}}>{address.line1}</span><br/>
                <span style={{fontSize:'12px'}}>{address.line2}</span><br/>
                <span style={{fontSize:'12px'}}>{address.line3}</span>
                
                {reviews.length > 0 && <h4>{`Reviews: ${reviews.length}`}</h4>}
                {
                    contol&&<div><Button>Edit</Button>
                    <Button>Delete</Button></div>}

            </div>
        </div>
    )
}

export default Card
