import React, {useState, useEffect} from 'react'
import Card from '../components/Card';
import axios from 'axios';

const name = localStorage.getItem('name');

function MyListings() {
  const [listings, setlistings] = useState([]);
  useEffect(() => {
    const fn = async() => {
      let resp = await axios.get(`http://localhost:5005/listings`);
      if(resp.status === 200){
        let ls = [];
        console.log(name)
        for(let i=0; i<resp.data.listings.length; i++){
            if(resp.data.listings[i].owner===name){
                ls.push(resp.data.listings[i]);
            }
            
        }
        console.log(resp.data.listings)
        console.log(ls);
        setlistings([...ls]);
        
      }
    }
    fn();
  }, [])


    return (
        <div style={{marginTop:"100px"}}>
            {listings.map((e, i)=>{
              return(<Card key={i} thumbnail={e.thumbnail} title={e.title} price={e.price} reviews={e.reviews} address={e.address} control={2}/>)})}
        </div>
    )
}

export default MyListings
