import React from 'react'
import './Card.css'

function Card({ thumbnail, title, price, reviews }) {
    return (
        <div className='card'>
            <img src={thumbnail} alt="" />
            <div className="card__info">
                <h2>{title}</h2>
                <h2>{price}</h2>
                
                {reviews.length > 0 && <h4>{`Reviews: ${reviews.length}`}</h4>}
            </div>
        </div>
    )
}

export default Card
