import { AppBar, Avatar, Button, TextField } from '@mui/material'
import { useNavigate, Link } from "react-router-dom";

import axios from 'axios';
import React from 'react'



function Header({ user, dispatch }) {
    const token = localStorage.getItem('token');
    const navigate = useNavigate();
    const handleLogout = async (e) => {
        try {
            let response = await axios.post(`http://localhost:5005/user/auth/logout`, {}, { headers: { 'authorization': 'Bearer ' + token } });
            if (response.status === 200) {
                localStorage.removeItem('token');
                localStorage.removeItem('name');
                dispatch(
                    {
                        type: 'SET_USER',
                        user: null,
                    }
                )
                navigate("/");
            }
        } catch (err) {
            console.error(err);
        }
    }

    

    return (
        <AppBar style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', backgroundColor: 'grey'}}>
            <div style={{ marginLeft: "20px" }}>
                <Link to="/" style={{ textDecoration: "none", color: 'white', display:'flex',alignItems:'center' }}><img src="https://cdn-icons-png.flaticon.com/512/34/34870.png" style={{height:'30px',width:'30px', marginRight:'5px'}}/><h2>airbrb</h2></Link>
            </div>
            

            {user && <><div style={{ justifyContent: 'right', margin: '10px' }}>
            <Button  style={{ margin: '10px',backgroundColor:'grey', color:'white', fontWeight:'bold', borderColor:'black' }} onClick={() => { navigate('/properties') }}>All Properties</Button>
                <Button  style={{ margin: '10px',backgroundColor:'grey', color:'white', fontWeight:'bold'}} onClick={() => { navigate('/addListing') }}>Create Lists</Button>
                <Button  style={{ margin: '10px',backgroundColor:'grey', color:'white', fontWeight:'bold' }}  onClick={() => { navigate('/myBooking') }}>Live Book Requests</Button>
                <Button style={{ margin: '10px',backgroundColor:'grey', color:'white', fontWeight:'bold' }} onClick={() => { navigate('/myProperties') }}>My Properties</Button>
                
            </div></>}
            {user ? <Button label="Logout" onClick={(e) => { handleLogout(e) }} style={{ margin: '15px', height: "50px", color: "white" }} ><Avatar>M</Avatar> <h6 style={{marginLeft: "10px"}}>Logout</h6></Button>
                : <Button color="secondary" variant="contained" color="primary" style={{ margin: '10px',backgroundColor:'darkgray' }} onClick={() => { navigate("/login") }}>Sign In</Button>
            }

        </AppBar>
    )
}

export default Header
