import React, { useState, useRef } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import axios from 'axios';
import Container from '@mui/material/Container';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';

const emptyListing = {
    title: "",
    price: 0,
    addressLine1: "",
    addressLine2: "",
    addressLine3: "",
    thumbnail: "",
    metadata: {}
}

const token = localStorage.getItem('token');

function AddListing() {
    const [formValues, setFormValues] = useState(emptyListing);
    const ref = useRef(null);
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormValues({
            ...formValues,
            [name]: value,
        });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        let data = {
            title: formValues.title,
            price: formValues.price,
            address: {
                line1: formValues.addressLine1,
                line2: formValues.addressLine2,
                line3: formValues.addressLine3
            },
            thumbnail: formValues.thumbnail,
            metadata: formValues.metadata,
        };
        try {
            let response = await axios.post(`http://localhost:5005/listings/new`, data, {
                headers: {
                    authorization: 'Bearer ' + token
                }
            })
            if(response.statusCode === 200) {
                setFormValues({...emptyListing});
               e.target.reset();
            }

        } catch (e) {
            console.error(e);
        }

    }

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <form onSubmit={handleSubmit}>
                <Box
                    sx={{
                        marginTop: 15,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'left',
                    }}
                >

                    <TextField
                        id="title-input"
                        name="title"
                        label="Title"
                        type="text"
                        value={formValues.title}
                        onChange={handleInputChange}
                        style={{ marginBottom: "10px" }}
                    />
                    <TextField
                        id="thumbnail-input"
                        name="thumbnail"
                        label="Thumbnail"
                        type="text"
                        value={formValues.thumbnail}
                        onChange={handleInputChange}
                        style={{ marginBottom: "10px" }}
                    />
                    <TextField
                        id="price-input"
                        name="price"
                        label="Price"
                        type="number"
                        value={formValues.price}
                        onChange={handleInputChange}
                    />
                    <h4>Address</h4>
                    <TextField
                        id="addressLine1-input"
                        name="addressLine1"
                        label="Line 1"
                        type="text"
                        value={formValues.addressLine1}
                        onChange={handleInputChange}
                        style={{ marginBottom: "10px" }}
                    />
                    <TextField
                        id="addressLine2-input"
                        name="addressLine2"
                        label="Line 2"
                        type="text"
                        value={formValues.addressLine2}
                        onChange={handleInputChange}
                        style={{ marginBottom: "10px" }}
                    />
                    <TextField
                        id="addressLine3-input"
                        name="addressLine3"
                        label="Line 3"
                        type="text"
                        value={formValues.addressLine3}
                        onChange={handleInputChange}
                        style={{ marginBottom: "10px" }}
                    />
                    <Button variant="contained" color="primary" type="submit">
                        Submit
                    </Button>


                </Box>
            </form>
        </Container>
    )
}

export default AddListing
