import { AppBar, Avatar, Button, TextField } from '@mui/material'
import { useNavigate, Link } from "react-router-dom";
import Chip from '@mui/material/Chip';
import axios from 'axios';
import React from 'react'

const token = localStorage.getItem('token');

function Header({ user, dispatch }) {
    const navigate = useNavigate();
    const handleLogout = async(e)=>{
        try {
            let response = await axios.post(`http://localhost:5005/user/auth/logout`, {}, {headers: {'authorization': 'Bearer '+token}});
            if(response.status === 200){
                localStorage.setItem('token', null);
                navigate("/")
            }
        } catch (err) {

        }
    }

    return (
        <AppBar color="secondary" style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between'}}>
            <div style={{marginLeft:"20px"}}>
                <Link to="/" style={{textDecoration:"none", color: 'white'}}><h2>AIRBNB</h2></Link>
            </div>
            <div>
                <TextField id="outlined-basic" label="Search"  hiddenLabel={true} style={{backgroundColor:'#FFFFFF', borderRadius:"50px", margin:"10px", borderColor:"none"}}></TextField>
            </div>

            {user && <><div style={{justifyContent: 'right', margin: '10px'}}>
                <Button color="secondary" variant="contained" style={{margin:'10px'}}>My Bookings</Button>
                <Button color="secondary" variant="contained" style={{margin:'10px'}} onClick={()=> {navigate('/addListing')}}>Add Listings</Button>
                <Button color="secondary" variant="contained" style={{margin:'10px'}}>My Listings</Button>
                
            </div></>}
            {token?<Chip avatar={<Avatar >M</Avatar>} label="Logout" onClick={(e)=>{}} style={{margin:'15px', height:"50px", color:"white"}}/>
            : <Button color="secondary" variant="contained" style={{margin:'10px'}} onClick={()=>{navigate("/login")}}>Login</Button>
            }
            
        </AppBar>
    )
}

export default Header
