import React, { useState, useEffect } from 'react'
import axios from 'axios';
import Date from '../components/Date';
import { Button } from '@mui/material';
import StarIcon from '@mui/icons-material/Star';

const id = localStorage.getItem('id');

function Book() {
    const [listing, setlisting] = useState({});

    const handleBooking=()=>{
        const res= axios.post(``,{

        })
    }

    useEffect(() => {
        console.log('booknjsdksvdjsnvdjsnvkjdnskj')
        const fn = async () => {
            try {
                let res = await axios.get(`http://localhost:5005/listings/${id}`);
                console.log(res)
                if(res.status==200) {
                    setlisting({...res.data.listing});
                }

            } catch (e) {
                console.error(e);
                alert(e?.response?.data?.error);
            }
        }
        fn();

    }, []);

    return (
        <>
        <div style={{display:'flex',flexDirection:'column', justifyContent: 'center', background:'white', height:'200%'}}>
            {/* picture */}
            <img src={listing.thumbnail} alt="" style={{align: 'center', marginLeft:'auto', marginRight:'auto', width:"70%", height:"500px" }}/>
            <div>
                {/* Title */}
                <h1 style={{ marginLeft:"50px"}}>{listing.title}</h1>
                <div style={{}}>
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <br />
                </div>
                {/* Price */}
                <h2 style={{ marginLeft:"50px"}}>{"$ "+ listing.price+" per Night"}</h2>
            </div>

            <div style={{ marginLeft:"50px" }}>
                <p>{listing?.address?.line1}</p>
                <p>{listing?.address?.line1}</p>
                <p>{listing?.address?.line1}</p>
            </div>
            {/* Bedroom */}
            <h4 style={{ marginLeft:"50px"}}>Bedrooms: {listing?.metadata?.bedrooms}</h4>
            <h4 style={{ marginLeft:"50px"}}>Beds: {listing?.metadata?.bathrooms}</h4>
            <h4 style={{ marginLeft:"50px"}}>Bathrooms: {listing?.metadata?.bathrooms}</h4>
            
            <div style={{width:'200px', marginLeft:'50px', display:'flex', height:"50px"}}>
                <Date style={{zIndex:'100'}}/>
                <Button style={{ backgroundColor: 'red', marginLeft: '10px', borderRadius: "10px", color:'white' }} variant="outlined" onClick={()=>{
                    handleBooking()
                }} >Book</Button>
            </div>


        </div>
        <div style={{height: '500px', backgroundColor: 'white'}}></div></>
    )
}

export default Book
