import React from 'react'
import './Card.css'
import StarIcon from '@mui/icons-material/Star';
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import axios from 'axios';

function Card({data, handleDelete, ob}) {
    const navigate = useNavigate();

    const handlePublish = async(id)=>{
        try {
            let start = new Date.now();
            let end = new Date(new Date().getFullYear(), 11, 31);

            let response = await axios.put(`http://localhost:5005/listings/publish/${id}`, {
                "availability": [
                  {start: start, end: end}
                ]
              })
        } catch(err) {
            
        }
    }
    

    const handleUnpublished= async(id)=>{
        try{
            let response= await axios.put(`http://localhost:5005/listings/publish/${id}`,)
        }catch(err){
            
        }
    }
    return (
        <div className='card'>
            <img src={data.thumbnail} alt="" />
            <div className="card__info">
                <h2>{data.title}</h2>
                <h2>{data.price}</h2>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <StarIcon style={{color:'yellow', border:'1px solid black', margin: "1px"}}/>
                <br/>
                <span style={{fontSize:'12px'}}>{data.address.line1}</span><br/>
                <span style={{fontSize:'12px'}}>{data.address.line2}</span><br/>
                <span style={{fontSize:'12px'}}>{data.address.line3}</span>
                
                {data.reviews.length > 0 && <h4>{`Reviews: ${data.reviews.length}`}</h4>}
                {
                    data.control&&<div ><Button onClick={()=>{localStorage.setItem('listing',data.id.toString()); navigate('/editListing' )}}>Edit</Button>
                    <Button onClick={()=>{handleDelete(data.id)}}>Delete</Button>
                    {!ob[data.id].published && <Button>Publish</Button>}
                    {ob[data.id].published && <Button>Unpublish</Button>}
                    </div>}

            </div>
        </div>
    )
}

export default Card
