import React from 'react'
import './Card.css'
import StarIcon from '@mui/icons-material/Star';
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import axios from 'axios';

const token = localStorage.getItem('token');

function Card({ data, handleDelete, ob, fetchTrigger, setfetchTrigger }) {
    const navigate = useNavigate();

    const handlePublish = async (id) => {
        try {
            let start = new Date();
            let end = new Date(new Date().getFullYear(), 11, 31);

            let response = await axios.put(`http://localhost:5005/listings/publish/${id}`, {
                "availability": [
                    { start: start, end: end }
                ]
            }, {
                headers: {
                    authorization: 'Bearer ' + token
                }
            });

            if(response.status === 200){
                setfetchTrigger(fetchTrigger+1);
            }
        } catch (err) {
            console.error(err);
            alert(err?.response?.data?.error);
        }
    }


    const handleUnpublish = async (id) => {
        try {

            let response = await axios.put(`http://localhost:5005/listings/unpublish/${id}`, {}, {
                headers: {
                    authorization: 'Bearer ' + token
                }
            });

            if(response.status === 200){
                setfetchTrigger(fetchTrigger+1);
            }
        } catch (err) {
            console.error(err);
            alert(err?.response?.data?.error);
        }
    }
    return (
        <div className='card'>
            <img src={data.thumbnail} alt="" />
            <div className="card__info">
                <h2>{data.title}</h2>
                <h2>{data.price}</h2>
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                <br />
                <span style={{ fontSize: '12px' }}>{data.address.line1}</span><br />
                <span style={{ fontSize: '12px' }}>{data.address.line2}</span><br />
                <span style={{ fontSize: '12px' }}>{data.address.line3}</span>

                {data.reviews.length > 0 && <h4>{`Reviews: ${data.reviews.length}`}</h4>}
                {
                    data.control && <div ><Button onClick={() => { localStorage.setItem('listing', data.id.toString()); navigate('/editListing') }}>Edit</Button>
                        <Button onClick={() => { handleDelete(data.id) }}>Delete</Button>
                        {!ob[data.id]?.published && <Button onClick={() => { handlePublish(data.id)}}>Publish</Button>}
                        {ob[data.id]?.published && <Button onClick={() => { handleUnpublish(data.id)}}>Unpublish</Button>}
                    </div>}
                    {
                        !data.control&&<Button onClick={()=>{
                            localStorage.setItem('id',data.id)
                            console.log('xxxxxxxxxxxxxxxxxxxxxx')
                            navigate('/mylistings')
                        }}>Book</Button>
                    }


            </div>
        </div>
    )
}

export default Card
