import React from 'react'
import './BCard.css';
import Button from '@mui/material/Button'

function BCard({booking, data, handleAccept, handleDecline, handleDelete, fetchTrigger, setfetchTrigger}) {
    return (
        <div className='card'>
            {data[booking.listingId] && <img src={data[booking.listingId].thumbnail} alt="" />}
            <div className="card__info">
                <h4>{'Status: '+ booking?.status}</h4>
                <h4>{'TotalPrice: $ '+ booking?.totalPrice}</h4>
                <Button onClick={()=>{handleAccept(booking.id)}}>Accept</Button>
                <Button onClick={()=>{handleDecline(booking.id)}}>Deny</Button>
                <Button onClick={()=>{handleDelete(booking.id)
                    setfetchTrigger(fetchTrigger+1);
                    }}>Delete</Button>
            </div>
        </div>
    )
}

export default BCard
