import React, { useState, useEffect } from 'react'
import axios from 'axios';
import DateComp from '../components/DateComp';
import { Button } from '@mui/material';
import StarIcon from '@mui/icons-material/Star';

const id = localStorage.getItem('id');
const token = localStorage.getItem('token');

function Book() {
    const [listing, setlisting] = useState({});
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [price, setprice] = useState(0);

    const handleBooking = async (id) => {
        try {
            const diffTime = Math.abs(startDate - endDate);
            const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
            const res = await axios.post(`http://localhost:5005/bookings/new/{$id}`,
                {
                    "dateRange": { start: startDate, end: endDate },
                    "totalPrice": diffDays * listing.price
                }, {
                headers: {
                    authorization: 'Bearer ' + token
                }
            });

        }

        catch (e) {
            console.error(e);
            alert(e.response.data.error);
        }

    }

    useEffect(() => {
        console.log('booknjsdksvdjsnvdjsnvkjdnskj')
        const fn = async () => {
            try {
                let res = await axios.get(`http://localhost:5005/listings/${id}`);
                console.log(res)
                if (res.status == 200) {
                    setlisting({ ...res.data.listing });
                    setprice(res.data.listing.price);
                }

            } catch (e) {
                console.error(e);
                alert(e?.response?.data?.error);
            }
        }
        fn();

    }, []);

    useEffect(() => {
        const diffTime = Math.abs(startDate - endDate);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        setprice(listing.price*diffDays);
    }, [startDate, endDate]);

    return (
        <>
            <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', background: 'white', height: '200%' }}>
                {/* picture */}
                <img src={listing.thumbnail} alt="" style={{ align: 'center', marginLeft: 'auto', marginRight: 'auto', width: "70%", height: "500px" }} />
                <div>
                    {/* Title */}
                    <h1 style={{ marginLeft: "50px" }}>{listing.title}</h1>
                    <div style={{ display: 'flex' }}>
                        <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px", marginLeft: "50px" }} />
                        <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                        <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                        <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                        <StarIcon style={{ color: 'yellow', border: '1px solid black', margin: "1px" }} />
                        <br />
                    </div>
                    {/* Price */}
                    <h2 style={{ marginLeft: "50px" }}>{"$ " + price}</h2>
                </div>

                <div style={{ marginLeft: "50px" }}>
                    <p>{listing?.address?.line1}</p>
                    <p>{listing?.address?.line1}</p>
                    <p>{listing?.address?.line1}</p>
                </div>
                {/* Bedroom */}
                <h4 style={{ marginLeft: "50px" }}>Bedrooms: {listing?.metadata?.bedrooms}</h4>
                <h4 style={{ marginLeft: "50px" }}>Beds: {listing?.metadata?.bathrooms}</h4>
                <h4 style={{ marginLeft: "50px" }}>Bathrooms: {listing?.metadata?.bathrooms}</h4>

                <div style={{ width: '200px', marginLeft: '50px', display: 'flex', height: "50px" }}>
                    <DateComp style={{ zIndex: '100' }} startDate={startDate} endDate={endDate} setEndDate={setEndDate} setStartDate={setStartDate}/>
                    <Button style={{ backgroundColor: 'red', marginLeft: '10px', borderRadius: "10px", color: 'white' }} variant="outlined" onClick={() => {
                        handleBooking(listing.id)
                    }} >Book</Button>
                </div>


            </div>
            <div style={{ height: '500px', backgroundColor: 'white' }}></div></>
    )
}

export default Book
